import "./App";
import { useState } from "react";
import bakeryData from "./bakery-data.json";
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';

// function CategoryButtons({item, setFilter, filterCategory}) {
function CategoryButtons2({filterCategory2, setFilter2}) {
    return (
        
        <FormControl>
            <FormLabel id="demo-radio-buttons-group-label">Alphabet</FormLabel>
            <RadioGroup
                aria-labelledby="demo-radio-buttons-group-label"
                defaultValue="All"
                name="radio-buttons-group"
            >
                <FormControlLabel value="All" control={<Radio />} label="All" onClick={()=> {
                    setFilter2("All")
                    filterCategory2("All");
                }}/>
                <FormControlLabel value="a" control={<Radio />} label="a" onClick={()=> {
                    setFilter2("a")
                    filterCategory2("a");
                }}/>
                <FormControlLabel value="b" control={<Radio />} label="b" onClick={()=> {
                    setFilter2("b")
                    filterCategory2("b");
                }}/>
                <FormControlLabel value="c" control={<Radio />} label="c" onClick={()=> {
                    setFilter2("c")
                    filterCategory2("c");
                }}/>
            </RadioGroup>
      </FormControl>
        
    );
}

export default CategoryButtons2;
