// I got my card componet from this website:
// https://mui.com/material-ui/react-card/
import "./App";
import "./BakeryItem.css";
import { useState } from "react";
import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Checkbox from '@mui/material/Checkbox';
import Typography from '@mui/material/Typography';

function BakeryItem({item, handleCount}) {
    const [bool,setBool] = useState(true);

    function handleBool() {
        setBool(!bool);
    }

    return (
        <div class="Item">
            <Card sx={{ maxWidth: 250 }} >
                <CardMedia
                    component="img"
                    height="140"
                    image={item.image}
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {item.name}
                    </Typography>
                    <Typography sx={{ mb: 1.5 }}>
                        Price: ${item.price} |  Rating: {item.rating}/5.0
                    </Typography>
                    <Typography sx={{ mb: 1.5 }}>
                        Type: {item.category}
                    </Typography>
                    <Typography sx={{ mb: 1.5 }}>
                    Dietary Restrictions: {item.category2}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        {item.description}
                    </Typography>
                </CardContent>
                <CardActions class="addRemove">
                    <button onClick={() => {
                        handleCount(item.price,item.name,true)
                    }}>Add</button>

                    <button onClick={() => {
                        handleCount(-item.price,item.name,false)
                    }}>Remove</button>
                    {/* <Checkbox onClick={() => {
                        if (bool) {
                            handleCount(item.price,item.name,true)
                        }
                        else {
                            handleCount(-item.price,item.name,false)
                        }
                        handleBool();
                    }} /> */}
                    {/* <Typography sx={{ mb: 1.5 }}>
                        Add or remove this item
                    </Typography>  */}
                </CardActions>
            </Card>
        </div>

        // <div class="bakeItem">
        //     <img src={item.image} width="50%"/>
        //     <h3>{item.name}</h3>
        //     <h4>{item.category}</h4>
        //     <h4>{item.category2}</h4>
        //     <p>{item.description}</p>
        //     <p>${item.price}</p>
        //     <p>{item.rating}</p>
        //     <button onClick={() => addCounter(item.price,item.name)}> Add </button>
        // </div> 
    );
}

export default BakeryItem;
