import "./App";
import { useState } from "react";
import bakeryData from "./bakery-data.json";

function CategoryButtons({filterCategory, setList, categories}) {
    

    return (
        <div>
            {categories.map((val) => {
                const [bool,setBool] = useState(true);

                function handleBool() {
                    setBool(!bool);
                }
                return (
                    <button onClick={() => {
                        if (bool) {
                            filterCategory(val);
                            handleBool();
                        }else {
                            setList(bakeryData);
                            handleBool();
                        }
                    }}>
                        {val}
                    </button>
                )
            })}
            <button onClick={() => setList(bakeryData)}>
                All
            </button>
        </div> 
    );
}

export default CategoryButtons;
