// I got my radio Group button from this website:
// https://mui.com/material-ui/react-radio-button/

import "./App";
import { useState } from "react";
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import Checkbox from '@mui/material/Checkbox';

function CategoryButtons2({filterCategory, setFilter}) {
    const [booll,setBooll] = useState(true);

    function handleBooll() {
        setBooll(!booll);
    }

    function handleRemove(item) {
        setFilter(current => current.filter(element => {
            return element !== item;
            }),
        );
    }
    const arr = ["a", "b", "c"]
    return (
        <FormControl>
            <FormLabel>Brand</FormLabel>
            <div>
                <Checkbox onClick={()=> {
                    if (booll) {
                        setFilter(current => [...current, "a"]);
                        // filterCategory("a");
                    } else {
                        handleRemove("a");
                        // filterCategory("a a");
                    }
                    handleBooll(!booll);
                    filterCategory("a");
                }} /> a
                <Checkbox onClick={()=> {                    
                    if (booll) {
                        setFilter(current => [...current, "b"]);
                    } else {
                        handleRemove("b");
                    }
                    handleBooll(!booll);
                    filterCategory("b");}} /> b
                <Checkbox onClick={()=> {
                    if (booll) {
                        setFilter(current => [...current, "c"]);
                    } else {
                        handleRemove("c");
                    }
                    handleBooll(!booll);
                    filterCategory("c");
                }} /> c
            </div>
      </FormControl>
    );
    // return (
    //     <FormControl>
    //         <FormLabel>Brand</FormLabel>
    //         <RadioGroup defaultValue="All">
    //             <FormControlLabel value="All" control={<Radio />} label="All" onClick={()=> {filterCategory("All")}} />
    //             <FormControlLabel value="a" control={<Radio />} label="a" onClick={()=> {filterCategory("a")}} />
    //             <FormControlLabel value="b" control={<Radio />} label="b" onClick={()=> {filterCategory("b")}} />
    //             <FormControlLabel value="c" control={<Radio />} label="c" onClick={()=> {filterCategory("c")}} />
    //         </RadioGroup>
    //   </FormControl>
    // );
}

export default CategoryButtons2;
