import "./App";
import { useState } from "react";
import bakeryData from "./bakery-data.json";

function CategoryButtons({item, filterCategory, setList}) {
    const [bool,setBool] = useState(true);

    function handleBool() {
        setBool(!bool);
    }
    return (
        <div>
            <button onClick={() => {
                if (bool) {
                    filterCategory(item);
                } else {
                    setList(bakeryData);
                }
                setBool(!bool)
            }}>
                {item}
            </button>
        </div>
    );
