import logo from './logo.svg';
import './App.css';
import { useState } from "react";
import BakeryItem from './BakeryItem';
import bakeryData from "./bakery-data.json";
import CategoryButtons from "./CategoryButtons";
import CategoryButtons2 from "./CategoryButtons2";
import SortButtons from "./SortButtons";

bakeryData.forEach((item) => {
  item.image = process.env.PUBLIC_URL + "/" + item.image;
});


// I learned how to create a filter from this website:
// https://www.freecodecamp.org/news/how-to-make-a-filter-component-in-react/
function App() {

  const [count,setCount] = useState(0);
  const [stuff,setStuff] = useState([]);
  const [list, setList] = useState(bakeryData);
  const [sorted, setSorted] = useState("Price");
  const [filter1, setFilter1] = useState("All");
  const [filter2, setFilter2] = useState("All");

  const mysort = ["Price", "Rating"]
  const categories = ["Bread", "Cake", "Cookie"]
  const categories2 = ["a", "b", "c"]

  function handleCount(val,item,bool) {
    
    if (bool) {
      setStuff(old => [...old, item]);
      setCount(count + val);
    }
    if (!bool) {
      let index = stuff.findIndex((t) => t == item);
      if (index != -1) {
        setCount(count + val);
        setStuff([
          ...stuff.slice(0, index),
          ...stuff.slice(index + 1)
        ]);
      }
    }
  }

  function handelFilter1(item) {
    setFilter1(item);
  }

  function handelFilter2(item) {
    setFilter2(item);
  }

  function handleList(item) {
    setList(item);
  }

  function handleSort(item) {
    setSorted(item);
  }

  // function filterCategory() {
  //   let newList = bakeryData.filter((temp) => {
  //     if (filter1.length === 0 && filter2.length === 0) {
  //       return bakeryData;
  //     } else if (filter1.length === 1 && filter2.length === 0){
  //       return temp.category === filter1.at(-1);
  //     } else if (filter1.length === 0 && filter2.length === 1){
  //       return temp.category2 === filter2.at(-1);
  //     } else if (filter1.length === 1 && filter2.length === 1){
  //       return temp.category === filter1.at(-1) && temp.category2 === filter2.at(-1);
  //     } else {
  //       return [];
  //     }
  //   })
  //   setList(newList);
  // }
  // function filterCategory() {
  //   let newList = bakeryData.filter((temp) => {
  //     return temp.category === filter1[0];
  //   })
  //   setList(newList);
  //   // console.log(newList);
  // }

  function filterCategory1(cat) {
    handelFilter1(cat);
    if (cat == "All" && filter2 == "All") {
      setList(bakeryData);
    } else if (cat === "All") {
      const newList = bakeryData.filter((temp) => {
        return temp.category2 === filter2;
      })
      setList(newList);
    } else if (filter2 == "All") {
      const newList = bakeryData.filter((temp) => {
        return temp.category === cat;
      })
      setList(newList);
    } else {
      const newList = bakeryData.filter((temp) => {
        return temp.category === cat && temp.category2 === filter2;
      })
      setList(newList);
    }
  }

  // function filterCategory2(cat) {
  //   if (cat == "All" && filter1 == "All") {
  //     setList(bakeryData);
  //   } else if (cat === "All") {
  //     const newList = bakeryData.filter((temp) => {
  //       return temp.category === filter1;
  //     })
  //     setList(newList);
  //   } else if (filter1 == "All") {
  //     const newList = bakeryData.filter((temp) => {
  //       return temp.category2 === cat;
  //     })
  //     setList(newList);
  //   } else {
  //     const newList = bakeryData.filter((temp) => {
  //       return temp.category2 === cat && temp.category === filter1;
  //     })
  //     setList(newList);
  //   }
  // }
  function filterCategory2(cat) {
    handelFilter2(cat);
    let temp = cat.split(" ").length;
    if (temp == 0 && filter1 == "All") {
      setList(bakeryData);
    } else if (cat === "All") {
      const newList = bakeryData.filter((temp) => {
        return temp.category === filter1;
      })
      setList(newList);
    } else if (filter1 == "All") {
      const newList = bakeryData.filter((temp) => {
        return temp.category2 === cat;
      })
      setList(newList);
    } else {
      const newList = bakeryData.filter((temp) => {
        return temp.category2 === cat && temp.category === filter1;
      })
      setList(newList);
    }
  }
  // function filterCategory1(cat) {
  //   const newList = list.filter((temp) => {
  //     return temp.category === cat;
  //   })
  //   setList(newList);
  // }

  // function filterCategory2(cat) {
  //   const newList = list.filter((temp) => {
  //     return temp.category2 === cat;
  //   })
  //   setList(newList);
  // }

  // function changeSort(item) {
  //   if (sorted === "Price") {
  //     const newList = list.sort((a, b) => a.price - b.price);
  //     setList(newList);
  //   } else if (sorted === "Rating") {
  //     const newList = list.sort((a, b) => b.price - a.price);
  //     setList(newList);
  //   }
  // }

  const setOrder = (a,b) => {
    if (sorted === "Price") {
      return a.price - b.price;
    } else if (sorted === "Rating") {
      return b.rating - a.rating;
    }
  }

  return (
    <div className="App">
      <header className="App-header">
          <div class="TotalPrice">
            <h2>Total price: ${count}</h2>
          </div>
        {/* <img src={logo} className="App-logo" alt="logo" /> */}
        {/* <p>
          Edit <code>src/App.js</code> and save to reload.
        </p> */}
        {/* <CategoryButtons 
          filterCategory={filterCategory}
          setList={setList}
          categories={categories}
        /> */}
        {/* <FormLabel id="demo-radio-buttons-group-label">Food Type</FormLabel> */}
        {/* <CategoryButtons item="All" filterCategory={filterCategory1} setList={setList}/> */}
        {/* {categories.map((item) => (  */}
          {/* // <CategoryButtons item={item} setFilter={setFilter1} filterCategory={filterCategory}/> */}
        <div class="filters">
          <div>
            <CategoryButtons filterCategory={filterCategory1} setFilter1={setFilter1}/>
        {/* ))} */}
        <div>{filter1}</div>
        <div>{filter2}</div>
        <div>{sorted}</div>
        {/* <div>{list.name}</div> */}

            <CategoryButtons2 filterCategory={filterCategory2} setFilter2={setFilter2}/>
        {/* <FormLabel id="demo-radio-buttons-group-label">Alphabet</FormLabel> */}
        {/* <CategoryButtons item="All" filterCategory={filterCategory1} setList={setList}/> */}
        {/* {categories2.map((item) => (  */}
          {/* // <CategoryButtons item={item} setFilter={setFilter2} filterCategory={filterCategory}/> */}
          {/* <CategoryButtons item={item} filterCategory={filterCategory2} setList={setList}/> */}
        {/* ))} */}

            <SortButtons handleSort={handleSort} />
          </div>
          <div>
            Cart :
            {stuff.map((item) => (
              <li>{item}</li>
            ))}
        </div>
        </div>

        

        {/* <p> {categories}</p> */}
        <div class="List">
          {/* {list.sort((a, b) => a.price - b.price).map((item, index) => (  */}
          {list.sort(setOrder).map((item, index) => ( 
            <BakeryItem key={index} item={item} handleCount={handleCount}/>
          ))}
        </div>
        
        
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
