import logo from './logo.svg';
import './App.css';
import { useState } from "react";
import BakeryItem from './BakeryItem';
import bakeryData from "./bakery-data.json";
import CategoryButtons from "./CategoryButtons";
import SortButtons from "./SortButtons";

bakeryData.forEach((item) => {
  item.image = process.env.PUBLIC_URL + "/" + item.image;
});


// I learned how to create a filter from this website:
// https://www.freecodecamp.org/news/how-to-make-a-filter-component-in-react/
function App() {

  const [count,setCount] = useState(0);
  const [stuff,setStuff] = useState([]);
  const [list, setList] = useState(bakeryData);
  const [sorted, setSorted] = useState("Price");

  const mysort = ["Price", "Alphabet"]
  const categories = ["Bread", "Cake", "Cookie"]

  function handleCount(val,name) {
    setCount(count + val);
    if (!stuff.includes(name)) {
      setStuff(old => [...old, name]);
    }
  }

  function filterCategory(cat) {
    const newList = list.filter((temp) => {
      return temp.category === cat;
    })
    setList(newList);
  }

  function changeSort() {
    if (sorted === "Price") {
      const newList = list.sort((a, b) => a.price - b.price);
      setList(newList);
    }
    if (sorted === "Alphabet") {
      const newList = list.sort((a, b) => a.name - b.name);
      setList(newList);
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <div>
          <h2>Total price: ${count}</h2>
          <h2>Cart</h2>
          {stuff.map((item) => (
            <li>{item}</li>
          ))}
        </div>
        <img src={logo} className="App-logo" alt="logo" />
        {/* <p>
          Edit <code>src/App.js</code> and save to reload.
        </p> */}

        {/* <CategoryButtons 
          filterCategory={filterCategory}
          setList={setList}
          categories={categories}
        /> */}
        {categories.map((item) => ( 
          <CategoryButtons item={item} filterCategory={filterCategory} setList={setList}/>
        ))}

        <SortButtons 
          changeSort={changeSort}
          setSorted={setSorted}
          mysort={mysort}
        />

        <p> {categories}</p>
        {/* {list.sort((a, b) => a.price - b.price).map((item, index) => (  */}
        {list.map((item, index) => ( 
          <BakeryItem key={index} item={item} addCounter={handleCount}/>
        ))}
        
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
